function [ V, X ] = ValueAndPolicyFunctionsNoBounds( Av, Bv, beta, mu, rho, sigma )
    % Ugly code generated by Maple
    t1 = -mu + 1;
    t2 = t1 * rho;
    t3 = 1 - beta;
    t4 = sqrt(t3);
    t5 = mu + t2;
    t6 = rho + 1;
    t7 = rho * beta;
    t8 = -(t3 * (t7 - 2)) + t4 * (-beta * t6 + 2);
    t9 = 1 - beta + t4;
    t10 = 1 - t7 + t4;
    t11 = 1 - t7;
    t12 = 4;
    t13 = (sigma ^ 2);
    t14 = (-3 * mu + t12) * mu - t13;
    t15 = mu ^ 2;
    t16 = t15 - t13;
    t17 = t1 ^ 2;
    t18 = rho ^ 2;
    t19 = t15 / 2 - 1;
    t20 = mu - 2;
    t21 = 2 * t15;
    t22 = 2 * mu - 2 * t13;
    t23 = t11 * t4;
    t24 = beta * t18;
    t25 = 1 - t24;
    t26 = 2 * rho;
    t27 = 2 + 2 * t23 + (beta * (t24 - 1 - t26));
    t28 = t20 * t18;
    t25 = 1 / t25;
    t27 = 1 / t27;
    t11 = 1 / t11;
    t29 = 1 / t3;
    t30 = 1 / beta;
    t8 = 1 / t8;
    t31 = 1 / t10;
    t10 = 1 / t10;
    t10 = t9 * t10;
    t31 = (-(beta * t5) + t4 + 1) * t31 * t30;
    t9 = (1 / 2 - t24 / 2 + t4 / 2) * t9 * t27 * t25;
    F = ((-(1 + mu + t2) * beta + 2) * t4 + (t3 * (-beta * t5 + 2))) * t8;
    G = -t10;
    H = -t4 * t30;
    V11 = -(1 + t23 * (((t16 / 2 + t18 * ((t17 * rho) + t14 / 2)) * beta + (mu / 2 - 2) * mu + (rho * t19 - t17) * rho + t13) * beta + 1) - beta * ((beta * (t7 * (-t24 * (mu + t2 - sigma) * (mu + t2 + sigma) + 2 * t16 + ((((-t12 + mu) * mu + 3) * rho - t13 + 2) * rho + t14) * rho) - t22 - ((t20 ^ 2 * rho + t12 - t13 - t21) * rho + t15 + t22 + 1) * rho)) + (mu * t12) - (2 * t13) + (-2 * rho * t19 + (t1 * t12) + t21) * rho - t15 + 1) / 2) * t25 * t29 * t11 * t27;
    V1A = (2 + (beta * (-t7 * (t20 * rho * t6 + t24 * t5 - 2 * mu) - t26 - 1 - mu + t28)) + t23 * (-(mu - t28) * beta + 2)) * t27 * t25 * t11;
    V1B = t31;
    VAA = -t9;
    VAB = -t10 * t30;
    VBB = -t4 * (t30 ^ 2) / 2;
    
    X = cummax( cummax( max( 0, 1 - F - bsxfun( @plus, G * Av, H * Bv' ) ), 1 ), 2 );
    
    V = cummax( cummax( V11 + bsxfun( @plus, V1A * Av + VAA * ( Av .* Av ), V1B * Bv' + VBB * ( Bv .* Bv )' ) + bsxfun( @times, VAB * Av, Bv' ), 1 ), 2 );
end
